widget:
@import "imports"  
@import "params"  
@import "myvars" 
@import "vars"  


%#{${{name}}} 
	@extend %object 

	// Set _minor to default size 
	$sizes: null!default 
	@if $sizes 
		@each $size in $sizes 
			$sz: #{nth($size, 1)} 
			@if $sz != _minor 
				&.#{nth($size, 1)} 
					font-size: nth($size,2) 
					padding: nth($size,2)/3 nth($size,2)/2 
			@else 
				font-size: nth($size,2) 
				padding: nth($size,2)/3 nth($size,2)/2 




	// Modifiers 



	// Themes 
	$themes: null!default 
	@if $themes 
		@each $theme in $themes  

			$index: 1 
			@if length( $themes ) != 1 
				$index: index( $themes, $theme ) 

			$imp: "" 
			@if $theme == "-disabled-" 
				$imp: !important 


			@if $theme != "default" 
				&.#{$theme} 
					color: nth( $color-invert, $index ) 
					border-color: nth( $border-color-lighten, $index ) 

				&:hover 
					border-color: nth( $border-color-darken, $index )#{$imp} 

			@else 
				color: nth( $color-invert, $index ) 
				border-color: nth( $border-color-lighten, $index ) 

				&:hover 
					border-color: nth( $border-color-darken, $index )#{$imp} 




@if $dependent == null 
	.#{${{name}}} 
		@extend %#{${{name}}}
