widgetFinal:
@import "params"
 @import "imports"

 {{{data}}}

 @if type-of( $themes ) == list or $themes == default


	 @each $theme in $themes 

	  $index: 1 

	  @if length( $themes ) > 1 
		   $index: index( $themes, $theme ) 

	  %#{${{name}}}-#{$theme} 
		   @include {{name}}( $index ) 

	  @if $dependences == null or ( type-of( $dependences ) == list and index( $dependences, ${{name}} ) == false ) 

		   .#{getClassName( ${{name}}, $theme )} 
			    @extend %#{${{name}}}-#{$theme}

