widget:
${{name}}: -{{name}} 

@import "imports" 
@import "params" 
@import "vars" 
@import "myvars" 

@mixin {{name}}( $index ) 
	// All here 


$themes: null !default 

@if $themes 
	@if type-of( $themes ) == list 

		@each $theme in $themes 

			$index: index( $themes, $theme ) 

			// With each theme do 
			@if $dependent == null 
				@if $theme != "default" 
					.#{${{name}}}.#{$theme} 
						@include {{name}}( $index ) 
				@else 
					.#{${{name}}} 
						@include {{name}}( $index ) 
			@else 
				%#{${{name}}}.#{$theme} 
					@include {{name}}( $index ) 

	@else 
		@if type-of( $themes ) == string 
			@if $dependent == null 
				@if $theme != "default" 
					.#{${{name}}}.#{$theme} 
						@include {{name}}( $index ) 
				@else 
					.#{${{name}}} 
						@include {{name}}( $index ) 
			@else 
				%#{${{name}}}.#{$theme} 
