widgetFinal:
@import "params"
 @import "imports"

 {{{data}}}

 @if type-of( $themes ) == list or $themes == default


	 @each $theme in $themes 

	  $index: 1 

	  @if length( $themes ) > 1 
		   $index: index( $themes, $theme ) 

	  %#{${{name}}}-#{$theme} 
		   @include {{name}}( $index ) 

	  @if $dependences == null or ( type-of( $dependences ) == list and index( $dependences, ${{name}} ) == false ) 

		   .#{getClassName( ${{name}}, $theme )} 
			    @extend %#{${{name}}}-#{$theme}

widget:
${{name}}: -{{name}} 

@import "params" 
@import "imports" 

@mixin {{name}}( $index ) 
	// All here 

@if type-of( $themes ) == list 

	@each $theme in $themes 

		$index: index( $themes, $theme ) 

		// With each theme do 
		@if $dependent == null 
			.#{${{name}}}.#{$theme} 
		@else 
			%#{${{name}}}.#{$theme} 

			// and here 
			@include {{name}}( $index ) 
@else 
	@if type-of( $themes ) == string 
		@if $dependent == null 
			.#{${{name}}}.#{$themes} 
				@include {{name}}( 1 ) 
		@else 
			%#{${{name}}}.#{$themes} 
				@include {{name}}( 1 )
